	(71454, 3, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Этого требует Вождь!', NULL, NULL),
	(71454, 4, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Гаррош дает мне силу!', NULL, NULL),
	(71454, 4, 1, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Слабаки!', NULL, NULL),
